(function(a){tinymce.PluginManager.requireLangPack('rcode');a.create("tinymce.plugins.RcodePlugin",{init:function(b,c){b.addCommand("mceEmotion",function(){b.windowManager.open({file:c+"/rcode.htm",width:580,height:400,inline:1},{plugin_url:c})});b.addButton("rcode",{title:"rcode.desc",image:c+'/img/smiley-cool.gif',cmd:"mceEmotion"})},getInfo:function(){return{longname:"Rcode",author:"lxyluu",authorurl:"http://tinymce.moxiecode.com",infourl:"http://wiki.moxiecode.com/index.php/TinyMCE:Plugins/emotions",version:a.majorVersion+"."+a.minorVersion}}});a.PluginManager.add("rcode",a.plugins.RcodePlugin)})();